class Solution(object):
    def countBits(self, num):
        """
        :type num: int
        :rtype: List[int]
        [1] = [0]+1 # 1 = '0' + 一個 1
        [2] = [0]+1 # 10 = '0' + 左側一個 1
        [3] = [1]+1 # 11 = '1' + '1' = [1] + 1
        [4] = [0]+1 # 100 = '0' + '1' = [0] + 1
        [5] = [1]+1 # 101 = '1' + '1' = [1] + 1
        [6] = [2]+1 # 110 = '10' + '1' = [2] + 1
        [7] = [3]+1 # 111 = '11' + '1' = [3] + 1

        1 % 1 =0
        2 % 1 =0 , then 1*2
        3 % 2 =1
        4 % 2 =0 , then 2*2
        5 % 4 =1
        6 % 4 =2
        """
        basicArray = [0]
        count = 1
        for i in range(1, num+1):
            if i == count << 1:
                count = count << 1
            basicArray.append(basicArray[i % count]+1)
        return basicArray
        
s = Solution()

assert s.countBits(0) == [0] # 0
assert s.countBits(1) == [0, 1] # 1
assert s.countBits(2) == [0, 1, 1] # 10
assert s.countBits(3) == [0, 1, 1, 2] # 11
assert s.countBits(4) == [0, 1, 1, 2, 1] # 100
assert s.countBits(5) == [0, 1, 1, 2, 1, 2]
assert s.countBits(6) == [0, 1, 1, 2, 1, 2, 2]
assert s.countBits(7) == [0, 1, 1, 2, 1, 2, 2, 3]
assert s.countBits(8) == [0, 1, 1, 2, 1, 2, 2, 3, 1]
assert s.countBits(9) == [0, 1, 1, 2, 1, 2, 2, 3, 1, 2]
assert s.countBits(10) == [0, 1, 1, 2, 1, 2, 2, 3, 1, 2, 2]
assert s.countBits(11) == [0, 1, 1, 2, 1, 2, 2, 3, 1, 2, 2, 3]
assert s.countBits(12) == [0, 1, 1, 2, 1, 2, 2, 3, 1, 2, 2, 3, 2]
assert s.countBits(13) == [0, 1, 1, 2, 1, 2, 2, 3, 1, 2, 2, 3, 2, 3]
assert s.countBits(14) == [0, 1, 1, 2, 1, 2, 2, 3, 1, 2, 2, 3, 2, 3, 3]
